import ic.jms.genesis.employees.ProductManager;
import ic.jms.genesis.employees.TechnicalLeader;
import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.junit.MockitoJUnitRunner;

import java.util.concurrent.CountDownLatch;

@RunWith(MockitoJUnitRunner.class)
public class CallCenterTest {
    @Mock
    private Employee specialEmployee = new TechnicalLeader(10);

    @InjectMocks
    CallCenter callCenter = new CallCenter();

    @Before
    public void before(){
        callCenter.specialEmployee = specialEmployee;
        specialEmployee.finishCall();
    }

    @Test
    public void testCallCenter_ReceiveACall() throws InterruptedException {
        final CountDownLatch latch = new CountDownLatch(1);
        for(int i=0 ;i < 100 ;i++){
            callCenter.receiveACall();
        }
        latch.await();
    }
}
